import React from 'react';
import { useSelector } from 'react-redux';
import MovieListItem from './MovieListItem';
import MovieFooter from './MovieFooter';

const MovieList = () => {
  const movies = useSelector(state => state.movies);

  const filteredMovies = movies.filter(movie => {
    return true;
  });

  return (
    <div className="flex-1">
      <div className="overflow-hidden bg-white rounded-md shadow mb-4 sm:min-h-[400px]">
        <table className="table-auto border-collapse text-left w-full">
          <thead>
            <tr className='border-zinc-200 border-b'>
              <th className='pl-4'>İsim</th>
              <th>Yönetmen</th>
              <th>Tür</th>
              <th>Metascore</th>
              <th></th>
            </tr>
          </thead>

          <tbody className='text-sm'>
            {
              filteredMovies.map(movie => <MovieListItem key={movie.id} movie={movie} />)
            }
          </tbody>
        </table>
      </div>

      <MovieFooter totalMovies={filteredMovies.length} />
    </div>
  );
}

export default MovieList;
